import React, { Component } from 'react';
import ContentContainer from '../../hoc/ContentContainer/ContentContainer';
import Select from '../Widgets/Select/Select';
import TextInput from '../Widgets/TextInput/TextInput';
import './AgentFilter.scss';
import data from '../../db';


const AgentFilter = (props)=>{
  const getCityOptions = ()=>{
    const options = data.agents.map(agent =>{
      return({
        value:agent.city,
        content:agent.city
      });
    });
    return(
      options.filter((option,index)=>{
        console.log(options.indexOf(option),"using index to find object")
        return options.indexOf(option) >= index;
      })
    )
  };

  console.log(getCityOptions(),"these are the cities")
    return(
        <div className="agent-filter">
          <ContentContainer className = "agent-filter__input-container">
            <TextInput
              name ="agentName"
              placeholder="filter by agent name"
              className="agent-filter__text-input"
              size="small"
              handleUserInput={props.handleUserInput}
              value={props.agentName}
              border
            />

            <Select
              name ="agent-city"
              className="agent-filter__select"
              border size="small"
              options={getCityOptions()}
              onChange={props.onChange}
              value={props.city}
            />

          </ContentContainer>
        </div>
    )
};

export default AgentFilter;
