import React from "react";
import Slider from "react-slick";
import './slider.scss';
import { Link } from 'react-router-dom';
import Card from '../Card/Card';


const SliderTemplates = (props) => {
    let template = null;
    const settings = {
      dots: true,
      infinite: true,
      speed: 800,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay:true,
      autoplaySpeed:10000,
      ...props.settings
    }

    switch(props.type){
        case('city'):
          template = props.data.map((item,i) => {
            return(
              <div key ={i} className = "page">
               <div className = "page__image" style = {{backgroundImage:`url("/images/city-section/slider-images/${item.image}")`}}></div>
               <div className = "page__description">
               <h1> {item.name} </h1>
               <p> {item.description}</p>
               <button className = "page__btn">View Listings</button>
               </div>

              </div>
            )
          })
          break;
        case('agent'):
          console.log(props.data, "agents");
          template = props.data.map((agent,i) => {
            console.log(agent.image)
            return(
              <Link to={"/about/4"} draggable="false" onClick={()=>{console.log("link was clicked!")}}>
                <Card type = "agent" data ={agent}> </Card>
              </Link>
            )
          })
          break;
        case('newListings'):
          template = props.data.map((listing,i) => {
            return(
              <Link draggable = 'false'>
                <Card type = "listing" data ={listing}> </Card>
              </Link>
            )
          })
          break;
        default:
          template = null;
    }

    return (
      <div className ={`${props.type}-template`}>
        <Slider {...settings}>
          {template}
        </Slider>
      </div>
    );
}

export default SliderTemplates;
