import React, { Component } from 'react';
import './Select.scss';


const Select = (props)=> {

  const options = props.options || null;

  const renderOptions = ()=>{
    if(props.options){
      return (
        props.renderOptions.map((option)=>{
          return(
            <option value = {option.value}> {option.content} </option>
          )
        })
      )
    }
    return null
  }

  console.log(renderOptions(),"these are the options");



  return(
    <div
    className = {`select ${props.border ? 'select--border' : null} ${props.size === "small" ? 'select--small': null} ${props.className}`}
    onChange = {props.onChange}
    >
      <select name={props.name}>
        {renderOptions()}
      </select>
    </div>
  )

}

export default Select;
